INSERT INTO "public"."Post" ("txid", "amount", "handle_id", "content", "created_at", "locked_until", "media_url") SELECT 'tx_' || md5(random()::text), (random() * 1000000000)::BIGINT, 'anon', CASE (random() * 4)::INT WHEN 0 THEN 'Just locked some BSV! 🔒 Feeling bullish about this project. #BSV #Crypto' WHEN 1 THEN 'Amazing developments in the BSV ecosystem. The future is bright! 💫' WHEN 2 THEN 'Long-term holder here. Just increased my position. 📈 #HODL' WHEN 3 THEN 'Technical analysis looking good. Who else is excited? 🚀' ELSE 'Building the future of Bitcoin SV, one lock at a time. 🏗️' END, NOW() - (random() * INTERVAL '30 days'), extract(epoch from (NOW() + INTERVAL '1 day' * (random() * 30)::INT))::BIGINT, CASE WHEN random() > 0.7 THEN 'https://picsum.photos/id/' || (random() * 1000)::INT || '/800/600' ELSE NULL END FROM generate_series(1, 100);
